import React from 'react';
import handTrack from './Handshake';

class HandshakeClass extends React.Component {
  constructor(props) {
    super(props);
    // create a ref to store the textInput DOM element
    this.textInput = React.createRef();
    this.focusTextInput = this.focusTextInput.bind(this);
    this.toggleVideo = this.toggleVideo.bind(this);
    this.isVideo = React.createRef();
    this.updateNote = React.createRef();
    this.startVideo = this.startVideo.bind(this);
    this.video = React.createRef();
    this.runDetection = this.runDetection.bind(this);
    this.model = React.createRef();
    this.canvas = React.createRef();
    this.context = React.createRef();


  }

  focusTextInput() {
    // Explicitly focus the text input using the raw DOM API
    // Note: we're accessing "current" to get the DOM node
    this.textInput.current.focus();
  }

  toggleVideo() {
    if (!this.isVideo) {
        this.updateNote.innerText = "Starting video"
        this.startVideo();
    } else {
        this.updateNote.innerText = "Stopping video"
        handTrack.stopVideo(this.video)
        this.isVideo = false;
        this.updateNote.innerText = "Video stopped"
    }
}

startVideo() {
  handTrack.startVideo(this.video).then(function (status) {
      console.log("video started", status);
      if (status) {
          this.updateNote.innerText = "Video started. Now tracking"
          this.isVideo = true
          this.runDetection()
      } else {
          this.updateNote.innerText = "Please enable video"
      }
  });
}

runDetection() {
  this.model.detect(this.video).then(predictions => {
      console.log("Predictions: ", predictions);
      this.model.renderPredictions(predictions, this.canvas, this.context, this.video);
      if (this.isVideo) {
          requestAnimationFrame(this.runDetection);
      }
  });
}
  componentDidMount() {
    this.video = this.refs.video;
    this.canvas = this.refs.canvas;
    this.context = this.canvas.getContext("2d");
    let trackButton = this.refs.trackbutton;
    let updateNote = this.refs.updatenote;

    this.isVideo = false;
    this.model = null;

    const modelParams = {
      flipHorizontal: true,   // flip e.g for video  
      maxNumBoxes: 20,        // maximum number of boxes to detect
      iouThreshold: 0.5,      // ioU threshold for non-max suppression
      scoreThreshold: 0.6,    // confidence threshold for predictions.
    }



  // Load the model.
  handTrack.load(modelParams).then(lmodel => {
      // detect objects in the image.
      this.model = lmodel
      updateNote.innerText = "Loaded Model!"
      trackButton.disabled = false
  });

  }



  render() {
    // tell React that we want to associate the <input> ref
    // with the `textInput` that we created in the constructor
    return (
      <div>
            <button onClick={this.toggleVideo()} id="trackbutton" class="bx--btn bx--btn--secondary" type="button">
            Toggle Video
          </button>
        <div id="updatenote" class="updatenote mt10"> loading model ..</div>
        <video class="videobox canvasbox" autoplay="autoplay" id="myvideo"></video>
        <canvas ref="canvas" width={640} height={425} />
        <input
          type="text"
          ref={this.textInput} />

        <input
          type="button"
          value="Focus the text input"
          onClick={this.focusTextInput}
        />
      </div>
    );
  }
}

export default HandshakeClass;
